mapper.select:
select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.remark_name, u.user_type, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, remark_name = #{remarkName}, remark_name = null, user_type = #{userType}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, amount = #{amount}, score = #{score}, invite_code = #{inviteCode}, pay_password = #{payPassword}, real_name = #{realName}, idcard_no = #{idcardNo}, idcard_front_img = #{idcardFrontImg}, idcard_back_img = #{idcardBackImg}, wallet_address = #{walletAddress}, open_id = #{openId}, wallet_address = #{walletAddress}, three_ball_commission = #{threeBallCommission}, five_ball_commission = #{fiveBallCommission}, ten_ball_commission = #{tenBallCommission}, three_ball_cashback = #{threeBallCashback}, five_ball_cashback = #{fiveBallCashback}, ten_ball_cashback = #{tenBallCashback}, is_test = #{isTest}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}

update sys_user
amount = #{amount}, score = #{score}, update_by = #{updateBy}, update_time = sysdate()
where user_id = #{userId}

mapper.select:
SELECT p.user_id AS user_id, max(pu.child_cnt) AS child_cnt, max( p.user_name ) AS user_name, max( p.nick_name ) AS nick_name, max( p.remark_name) AS remark_name, max(p.avatar) AS avatar, IFNULL( sum( um.cash_money ), 0 ) AS total_commission_money, IFNULL( sum( tm.pre_commission_money ), 0 ) AS today_commission_money FROM ( SELECT p.parent_user_id AS p_user_id ,count(p.user_id) as child_cnt FROM sys_user p WHERE p.parent_user_id is not NULL AND p.parent_user_id != 2 GROUP BY p.parent_user_id )pu INNER JOIN sys_user p ON pu.p_user_id = p.user_id LEFT JOIN usermoney um ON um.user_id = p.user_id AND um.type = "11" LEFT JOIN ( SELECT uw.user_id AS user_id, SUM( ROUND(( uw.big_small_money + uw.other_money - uw.deduct_money )* g.game_commission / 100, 2 )) AS pre_commission_money FROM userwin uw INNER JOIN sys_game g ON g.game_id = uw.game_id WHERE DATE_FORMAT( uw.win_time, '%Y-%m-%d' )= CURDATE() GROUP BY uw.user_id ) tm ON tm.user_id = p.user_id left join sys_user pu on pu.user_id = p.parent_user_id WHERE p.del_flag = '0'
AND p.user_id = #{userId}
AND CONCAT(IFNULL(p.nick_name,''), IFNULL(p.remark_name,'')) like concat('%', #{nickName}, '%')
GROUP BY p.user_id
${params.dataScope}
ORDER BY p.user_id

SELECT c.user_id AS user_id, max(c.user_name ) AS user_name, max(c.nick_name ) AS nick_name, max(c.remark_name) AS remark_name, max(c.avatar) AS avatar, IFNULL( sum( um.cash_money ), 0 ) AS generate_commission_money FROM sys_user c LEFT JOIN usermoney um ON um.commission_from_user_id = c.user_id AND um.type = "11" WHERE c.del_flag = '0'
AND c.parent_user_id = #{parentUserId}
GROUP BY c.user_id ORDER BY c.user_id

mapper.update:
update sys_user
set
remark_name = #{remarkName}
remark_name = null
where user_id = #{userId}

update sys_user
set parent_user_id = #{mainUserId}
where parent_user_id = #{mergeUserId}
