mapper.select:

where DATE_FORMAT(win_time,'%Y-%m-%d')=CURDATE()
and game_id = #{gameId}

and user_id = #{userId}

limit 1

mapper.update:
update userwin
win_money = #{winMoney}, big_small_money = #{bigSmallMoney}, other_money = #{otherMoney}, combination_money = #{combinationMoney}, deduct_money = #{deductMoney}, cash_back_money = #{cashBackMoney}, commission_money = #{commissionMoney}, is_delete = #{isDelete}, update_by = #{updateBy}, update_time = sysdate()
where id = #{id}

update userwin
set is_delete = 1,update_time = sysdate()
where 1 = 1
 and user_id = #{userId}
 and game_id = #{gameId}

mapper.select:

WHERE
    is_delete = '0'
AND
    user_id = #{userId}
AND
    DATE_FORMAT(win_time,'%Y-%m-%d') = #{winTime}
 and game_id = #{gameId}

select count(1)
from ${betRecordTableName}
where user_id=#{userId}
and game_id = #{gameId}
and DATE_FORMAT(record_time, '%Y%m%d') = #{winTime}

mapper.update:
update userwin
set deduct_money = case when #{plusFlg} = '0' then deduct_money + #{countMoney} else deduct_money - #{countMoney} end
where user_id = #{userId}
and game_id = #{gameId}
and DATE_FORMAT(win_time,'%Y-%m-%d') = #{winTime}

update userwin
set cash_back_money = #{cashBackMoney},update_time = sysdate()
where id = #{id}

update userwin
set commission_money = #{commissionMoney},update_time = sysdate()
where id = #{id}
