mapper.select:

where DATE_FORMAT(win_time,'%Y-%m-%d')=CURDATE()
and game_id = #{gameId}

and user_id = #{userId}

limit 1

mapper.update:
update userwin
win_money = #{winMoney}, big_small_money = #{bigSmallMoney}, other_money = #{otherMoney}, combination_money = #{combinationMoney}, deduct_money = #{deductMoney}, cash_back_money = #{cashBackMoney}, commission_money = #{commissionMoney}, is_delete = #{isDelete}, is_cash_back = #{isCashBack}, is_commission = #{isCommission}, update_by = #{updateBy}, update_time = sysdate()
where id = #{id}

update userwin
set is_delete = 1,update_time = sysdate()
where 1 = 1
 and user_id = #{userId}
 and game_id = #{gameId}

mapper.select:

WHERE
    is_delete = '0'
AND
    user_id = #{userId}
AND
    DATE_FORMAT(win_time,'%Y-%m-%d') = #{winTime}
 and game_id = #{gameId}

SELECT uw.user_id as user_id ,max(u.nick_name) as nick_name ,max(u.real_name) as real_name ,max(u.remark_name) as remark_name ,max(u.amount) as user_money ,uw.win_time as win_time ,uw.game_id as game_id ,max(uw.game_name) as game_name ,max(g.game_mark_id) as game_mark_id ,max(g.game_record) as game_record ,SUM(uw.win_money) AS win_money ,SUM(uw.big_small_money) AS big_small_money ,SUM(uw.other_money) AS other_money ,SUM(uw.combination_money) AS combination_money ,SUM(uw.deduct_money) AS deduct_money ,SUM(uw.cash_back_money) AS cash_back_money FROM userwin uw inner join sys_user u on u.user_id = uw.user_id and u.del_flag = '0' inner join sys_game g on g.game_id = uw.game_id WHERE 1 = 1 and uw.user_id = #{userId}
AND CONCAT(IFNULL(u.nick_name,''), IFNULL(u.remark_name,'')) like concat('%', #{nickName}, '%')
and date_format(uw.win_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
and date_format(uw.win_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
and DATE_FORMAT(uw.win_time, '%Y-%m-%d') = #{filterDay}
GROUP BY uw.user_id,uw.win_time,uw.game_id ORDER BY uw.win_time,uw.game_id

select count(1)
from ${betRecordTableName}
where user_id=#{userId}
and game_id = #{gameId}
and DATE_FORMAT(record_time, '%Y%m%d') = #{winTime}

mapper.update:
update userwin
set deduct_money = case when #{plusFlg} = '0' then deduct_money + #{countMoney} else deduct_money - #{countMoney} end
where user_id = #{userId}
and game_id = #{gameId}
and DATE_FORMAT(win_time,'%Y-%m-%d') = #{winTime}

update userwin
set cash_back_money = #{cashBackMoney},is_cash_back = '1',update_time = sysdate()
where id = #{id}

update userwin
set commission_money = #{commissionMoney},is_commission = '1',update_time = sysdate()
where id = #{id}
